import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import "./login.css"

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      // Make a GET request to your backend API to retrieve the user record that matches the entered username
      const userRecord = await axios.get(`http://localhost:3000/api/user_login?username=${username}`);

      if (userRecord.data.length === 0) {
        // If no user record is found, set an error message
        setErrorMessage('Invalid username or password');
        return;
      }

      // Check if the entered password matches the password of the retrieved user record
      if (userRecord.data[0].password === password) {
        // If the passwords match, redirect to the homepage
        window.location.href = '/home';
      } else {
        // If the passwords don't match, set an error message
        setErrorMessage('Invalid username or password');
      }
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="form-container">
      <h1>Login</h1>
      <form onSubmit={handleLogin} >
        <label>
          Username:
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </label>
        <br />
        <label>
          Password:
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </label>
        <br />
        {/* <Link to='/home'>
           <button type="submit">Login</button>
        </Link> */}
           {errorMessage && <p>{errorMessage}</p>}
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
