function Table({data}) {

    return (

        <div className='table-sec'>
            <table className='blueTable'>
                <thead>
                    <tr>
                        <th>Date</th>
                        <th>Time</th>
                        <th>Pressure Value</th>
                        <th>Technician Name</th>
                        <th>Job Number</th>
                        <th>Serial Number</th>
                        {/* <th>Pass/Fail</th> */}
                        <th>Notes</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((row ) => (
                        <tr key={row.pressure_id}>
                            <td>{row.date_collected}</td>
                            <td>{row.time_collected}</td>
                            <td>{row.pressure_value}</td>
                            <td>{row.tech_name}</td>
                            <td>{row.job_num}</td>
                            <td>{row.serial_num}</td>
                            {/* <td>{row.pass_fail}</td> */}
                            <td>{row.notes}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    )
}

export default Table;













