import './table.css';
import Search from '../search/search';
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Table() {


    const [data, setData] = useState([]);
    const [query, setQuery] = useState('');

    useEffect(() => {
        async function fetchData() {
            const result = await axios('/data');
            setData(result.data);
        }
        fetchData();
    }, []);

  // Filter the data based on the query string
    const filteredData = data.filter((row) => {
    const date = new Date(row.date); // Assuming the date is stored as a string in ISO format
    const queryDate = new Date(query);

    // Check for a match against the date column or time column
    return date.toDateString() === queryDate.toDateString() || date.toLocaleTimeString().includes(query);
  });


    return (

        <div>

            <Search />
            <table>
            <thead>
                <tr>
                    <th>Date</th>
                    <th>Time</th>
                    <th>Technician Name</th>
                    <th>Pressure Value</th>
                    <th>Pass/Fail</th>
                </tr>
            </thead>
            <tbody>
                {filteredData.map((row) => (
                    <tr key={row.id}>
                        <td>{row.id}</td>
                        <td>{row.name}</td>
                        <td>{row.email}</td>
                        <td>{row.date}</td>
                    </tr>
                ))}
            </tbody>
            </table>
        </div>
    )
}

export default Table;
